ruleToc.chapterList:
@js:
var the="dl dd a";
var juan="dl dt";
var th=/第[〇零一二三四五六七八九十百]+卷.*|第0+章|.*内容简介/g;
eval(String(source.loginUrl));
v==true||false?is(the,juan,th):no(the,th);

function is(path,volume,regex){
	dl=path+","+volume;
	d=java.getElements(dl).toArray();
	list=d.map((a,i)=>{
		name=a.select("a").text();
		link=a.select("a").attr("href")
		dt=a.select("dt").text();
		title=a.attr("title");
		name=String(name).replace(regex,"")
		j={
			text:name,
			href:link,
			volume:dt,
			info:title
			};
		return JSON.stringify(j)
		})
		return list
